import React, { Component } from 'react'
import { withRouter } from 'next/router'
import { Row, Col, Carousel, Button, Image } from 'antd'
import moment from 'moment'
import style from './style.module.css'
import { VerticalAlignBottomOutlined } from '@ant-design/icons'
import { carousePage } from '@/api/index.js'
import Layout from './../Layouts'
class ImgBlock extends Component {
  constructor(props) {
    super(props)
    const { router, pageListData, CarouselList } = props

    this.state = {
      poolData: CarouselList || [],
      pageList: pageListData || [],
      windowWidth: 0,
      router,
      loadings: false,
      query: {
        size: 12,
        page: 1,
        desc: true
      }
    }
  }
  componentDidMount() {
    this.setState({
      windowWidth: window.innerWidth
    })
  }
  onLoadMore = () => {
    const currentPage = this.state.query.page + 1
    this.setState({
      loadings: true,
      query: {
        page: currentPage,
        size: 12,
        desc: true
      }
    })
    carousePage({
      size: 12,
      page: currentPage,
      desc: true
    }).then(res => {
      if (res && res.success) {
        this.setState({
          pageList: [...this.state.pageList, ...res.data]
        })
        this.setState({
          loadings: false,
        })
      }
    })
  }
  render() {

    return (
      <Layout active='/'>
        <div>
          <Carousel autoplay={true}>

            {this.state.poolData && this.state.poolData.map((item) => (
              <div key={item.id}>
                <Image
                  className={style.imgStyle}
                  width={'100%'}
                  alt={item.describe}
                  placeholder={
                    <Image
                      preview={false}
                      src={'https://xek.dlsjf.top/' + item.name + '-123'}
                      width={'100%'}
                    />
                  }
                  preview={false}
                  src={'https://xek.dlsjf.top/' + item.name}
                  fallback="data:image/png;base64,"
                />
              </div>
            ))}
          </Carousel>
          <div style={{ margin: '20px 0' }}>
            {/* <Button onClick={this.getCarouselListAll} type="primary">
              换一批
            </Button> */}
            <span>&nbsp;本站图片均来自必应</span>
          </div>
          <div style={{ overflow: 'hidden' }} >
            <Row gutter={[16, 20]}>
              {this.state.pageList && this.state.pageList.map((item) => (
                <Col span={this.state.windowWidth > 500 ? 6 : 12} key={item.id}>
                  <div className={style.imgblock} onClick={() => {
                    this.state.router.push(`/Home/ImageDetail?data=${JSON.stringify(item)}`)
                  }}>
                    <Image
                      className={style.imgWidth}
                      width={'100%'}
                      alt={item.describe ? JSON.parse(item.describe) : item.describe}
                      placeholder={true}
                      preview={false}
                      src={'https://xek.dlsjf.top/' + item.name + '-123'}
                      fallback="data:image/png;base64,"
                    />
                    <div className={style.apron}>
                      <div className={style.descText}>
                        {item.describe.replace(/"/g, '')}
                      </div>
                      <div className={style.info}>
                        <div>
                          {' '}
                          {moment(item.create_date).format('YYYY-MM-DD')}{' '}
                          <a
                            style={{ float: 'right' }}
                            // eslint-disable-next-line react/jsx-no-target-blank
                            target="_blank"
                            href={'https://xek.dlsjf.top/' + item.name}
                            download={item.describe}
                          >
                            <VerticalAlignBottomOutlined />
                          </a>
                        </div>
                      </div>
                      {/* <HeartTwoTone style={{ color: 'red' }} /> */}
                    </div>
                  </div>
                </Col>
              ))}
            </Row>
          </div>
          <div style={{ height: '20px' }} />
          <Row>
            <Col span={6} offset={9}>
              <Button
                style={{ width: '100%', color: "#40a9ff", fontSize: '20px' }}
                type="text"
                loading={this.state.loadings}
                onClick={() => this.onLoadMore()}
              >
                加载更多
              </Button>
            </Col>
          </Row>
          <div style={{ height: '50px' }} />
        </div>
      </Layout>
    )
  }
}
export default withRouter(ImgBlock)
